#!/usr/bin/env node
'use strict';
var argv = require('minimist')(process.argv.slice(2));
var pkg = require('./package.json');
var ava = require('./');
var input = argv._;

function help() {
	console.log([
		pkg.description,
		'',
		'Usage',
		'  $ ava <file>',
		'',
		'Example',
		'  $ ava test.js'
	].join('\n'));
}

if (input.length === 0 || argv.help) {
	help();
	return;
}

if (argv.version) {
	console.log(pkg.version);
	return;
}

console.log('not yet implemented');
